#!/usr/bin/env python3
#
# From a JSON file generated by 'futhark bench', print on stdout runtimes for a
# benchmark and dataset, with each line denoting the elapsed time in seconds
# for a single run.

import sys
import json

_, json_fname, entry_point, dataset = sys.argv

J = json.load(open(json_fname, 'r'))

for x in J[entry_point]['datasets'][dataset]['runtimes']:
    print(x/1e6)
